var multiAttackSet = new GameDataEffectSet
{
    Effects = new WeightedLinkComplex<IGameLink<GameDataEffect>?>
    {
        Links = new[]
        {
            new WeightedLink<IGameLink<GameDataEffect>?> { Link = slashEffect, Weight = 60 },
            new WeightedLink<IGameLink<GameDataEffect>?> { Link = thrustEffect, Weight = 30 },
            new WeightedLink<IGameLink<GameDataEffect>?> { Link = kickEffect, Weight = 10 }
        }
    },
    SetFlags = new SetFlags
    {
        WeightedRandom = true,      // 启用加权随机
        PreferUnique = true,        // 优先选择不重复的效果
        MinCount = 2,               // 至少执行2个效果
        MaxCount = 3                // 最多执行3个效果
    }
};

